import React, { Component } from 'react';

class Board extends Component {

// players choose their own display name
// ...they also get to choose their display color...
// instead of X and O, just colored dots. players get to choose what color they want to be at the start.
// visually, blank spaces would be a small grey dot. once clicked, a space's dot would animate to get bigger and transform to user's chosen color

// play the computer? should be an option... just .random the AI placing in a spot...
// visually, menu should display:
// "Who's Playing?" <-- header/title at top of menu
// (human outline) vs. (computer image) <-- this indicates 1-player game vs. the AI
// (human outline) vs. (human outline) <-- this indicates 2-player game

// "best of '?' series", you can choose best 2 of 3, best of 5, best of 7, 9, 11, etc.

// add "player 1" and "player 2" areas on the left and right of the game board, have some kind of indicator that highlights whose turn it is (by selecting their side/section and making it glow or something). this section should also record how many total wins each player has. maybe first to 10 wins the game?

// to do: make it so the restart button doesn't appear UNLESS game is over

constructor(props){
  super(props)
  this.state = {
    turn: 0,
    winMessage: "",
    //whoseTurn: "X",
    board: Array(9).fill(""),
    xWinCount: 0,
    oWinCount: 0
  }
}


playerTurn(e){
  let {board, turn, winMessage, xWinCount, oWinCount} = this.state
  let id = e.target.id

  if(board[id] === ""){
    turn++
    if(turn % 2 === 0){
      board[id] = 'O'
      // whoseTurn = "X"
    } else {
      board[id] = 'X'
      // whoseTurn = "O"
    }
  }

  let winningArr = [[0,1,2] , [3,4,5] , [6,7,8] , [0,3,6] , [1,4,7] , [2,5,8] , [0,4,8] , [2,4,6]]
  let winner = winningArr.filter((combo, index, array )=>{
  let [a, b, c] = combo
  if ( board[a] === 'X' && board[b] === 'X' && board[c] === 'X' ) {
    winMessage = "X is Winner"
    turn = 10
    xWinCount++
    return combo
  } else if ( board[a] === 'O' && board[b] === 'O' && board[c] === 'O' ) {
    winMessage = "O is Winner"
    turn = 10
    oWinCount++
    return combo
  }
  if (turn === 9) {
    winMessage = "Stalemate!"
    return
  }
})

  this.setState({turn: turn, board: board, winMessage: winMessage, xWinCount: xWinCount, oWinCount: oWinCount})
}

restart(){
  this.setState ({
    turn: 0,
    winMessage: "",
    board: Array(9).fill("")
  })
}

render() {
  let {winMessage, xWinCount, oWinCount} = this.state
  let squares = this.state.board.map((val, index) => {

  if (winMessage === " ") {
    // dont display restart button
  } else {
    // display it
  }


    return  (
      <div onClick={this.playerTurn.bind(this)} key={index} id={index} className="grid-item">{val}</div>

    )
  })
  return (
      <div>
        <div className="Board">
          <div className="grid-container">{squares}</div>
        </div>
        <button onClick={this.restart.bind(this)}>Restart</button>
        <div>X wins: {xWinCount} | O wins:{oWinCount}{winMessage}</div>
    </div>
  );}
}



export default Board;
